import React, { useState, useEffect, useCallback } from 'react';
import { motion } from 'framer-motion';
import { useAuth } from '../contexts/AuthContext';
import {
  Bell,
  Eye,
  Upload,
  Download,
  MessageCircle,
  Search,
  Plus,
  LogOut,
  Calendar,
  DollarSign,
  Clock,
  CheckCircle,
  AlertCircle,
  XCircle,
  Pause,
  ArrowRight,
  TrendingUp,
  Activity,
  FileText,
  Users,
  Lock,
  Shield
} from 'lucide-react';
import { withdrawalRequestService } from '../services/withdrawalRequestService';
import { permissionService } from '../services/permissionService';
import { notificationService } from '../services/notificationService';
import NotificationContainer from './NotificationContainer';
import ConfirmDialog from './ConfirmDialog';
import {
  WithdrawalRequest,
  User,
  ActionType,
  DashboardStats,
  LoadingState,
  ModalState,
  FilterOptions
} from '../types/withdrawalTypes';

// Mock data for ADFD Withdrawal Request Tracker
const mockUsers = {
  'archive001': { id: 1, username: 'archive001', name: 'Ahmed Al Zaabi', role: 'archive', email: 'aalzaabi@adfd.ae', avatar: 'ðŸ‘¨â€ðŸ’¼' },
  'admin001': { id: 2, username: 'admin001', name: 'Mamadou Oury Diallo', role: 'loan_admin', email: 'Mamadouourydiallo819@gmail.com', avatar: 'ðŸ‘¨â€ðŸ’¼' },
  'ops001': { id: 3, username: 'ops001', name: 'Ali Al Derie', role: 'operations', email: 'aalderei@adfd.ae', avatar: 'ðŸ‘¨â€ðŸ”§' },
  'bank001': { id: 4, username: 'bank001', name: 'Ahmed Siddique', role: 'core_banking', email: 'asiddique@adfd.ae', avatar: 'ðŸ‘©â€ðŸ’»' }
};

interface WithdrawalRequest {
  id: number;
  projectNumber: string;
  country: string;
  refNumber: string;
  beneficiaryName: string;
  amount: number;
  currency: string;
  valueDate: string;
  status: string;
  currentStage: 'initial_review' | 'technical_review' | 'core_banking' | 'disbursed';
  assignedTo: number;
  createdBy: number;
  createdAt: string;
  updatedAt: string;
  processingDays: number;
  priority: 'urgent' | 'high' | 'medium' | 'low';
}

const mockRequests: WithdrawalRequest[] = [
  {
    id: 1001,
    projectNumber: '1001',
    country: 'UAE',
    refNumber: 'REF/001',
    beneficiaryName: 'Company Alpha',
    amount: 1200000,
    currency: 'USD',
    valueDate: '2025-05-12',
    status: 'Disbursed',
    currentStage: 'disbursed',
    assignedTo: 4,
    createdBy: 1,
    createdAt: '2025-05-10T09:00:00Z',
    updatedAt: '2025-05-15T14:30:00Z',
    processingDays: 5,
    priority: 'high'
  },
  {
    id: 1002,
    projectNumber: '1002',
    country: 'Egypt',
    refNumber: 'REF/002',
    beneficiaryName: 'Company Beta',
    amount: 850000,
    currency: 'EUR',
    valueDate: '2025-05-07',
    status: 'Pending due to expired withdrawal date; extension request sent to legal for review',
    currentStage: 'technical_review',
    assignedTo: 3,
    createdBy: 1,
    createdAt: '2025-05-05T10:15:00Z',
    updatedAt: '2025-05-08T16:20:00Z',
    processingDays: 15,
    priority: 'urgent'
  },
  {
    id: 1003,
    projectNumber: '1003',
    country: 'Jordan',
    refNumber: 'REF/003',
    beneficiaryName: 'Company Gamma',
    amount: 550000,
    currency: 'USD',
    valueDate: '2025-05-13',
    status: 'Pending with Operations for technical approval',
    currentStage: 'technical_review',
    assignedTo: 3,
    createdBy: 1,
    createdAt: '2025-05-11T11:30:00Z',
    updatedAt: '2025-05-12T09:45:00Z',
    processingDays: 8,
    priority: 'medium'
  },
  {
    id: 1004,
    projectNumber: '1004',
    country: 'Morocco',
    refNumber: 'REF/004',
    beneficiaryName: 'Company Delta',
    amount: 980000,
    currency: 'USD',
    valueDate: '2025-05-15',
    status: 'Awaiting authorized signature verification from beneficiary',
    currentStage: 'core_banking',
    assignedTo: 4,
    createdBy: 1,
    createdAt: '2025-05-13T14:20:00Z',
    updatedAt: '2025-05-14T11:10:00Z',
    processingDays: 6,
    priority: 'high'
  },
  {
    id: 1005,
    projectNumber: '1005',
    country: 'Tunisia',
    refNumber: 'REF/005',
    beneficiaryName: 'Company Epsilon',
    amount: 1500000,
    currency: 'EUR',
    valueDate: '2025-05-13',
    status: 'Pending with Core Banking; under disbursement review',
    currentStage: 'core_banking',
    assignedTo: 4,
    createdBy: 1,
    createdAt: '2025-05-11T16:45:00Z',
    updatedAt: '2025-05-13T13:25:00Z',
    processingDays: 7,
    priority: 'medium'
  }
];

const mockDocuments = {
  1001: [
    { id: 1, filename: 'Withdrawal_Form_REF001.pdf', fileSize: '2.3 MB', uploadedAt: '2025-05-10T09:15:00Z', uploadedBy: 1 },
    { id: 2, filename: 'Invoice_Alpha_001.pdf', fileSize: '1.8 MB', uploadedAt: '2025-05-10T09:16:00Z', uploadedBy: 1 }
  ],
  1002: [
    { id: 4, filename: 'Withdrawal_Form_REF002.pdf', fileSize: '2.1 MB', uploadedAt: '2025-05-05T10:20:00Z', uploadedBy: 1 },
    { id: 5, filename: 'Invoice_Beta_001.pdf', fileSize: '1.9 MB', uploadedAt: '2025-05-05T10:21:00Z', uploadedBy: 1 }
  ]
};

const mockComments = {
  1002: [
    { id: 1, userId: 3, comment: 'Withdrawal date has expired. Requesting extension from legal team.', createdAt: '2025-05-08T14:30:00Z' },
    { id: 2, userId: 2, comment: 'Extension request documentation sent to beneficiary.', createdAt: '2025-05-08T16:20:00Z' }
  ],
  1003: [
    { id: 3, userId: 3, comment: 'Technical review in progress. Checking project eligibility requirements.', createdAt: '2025-05-12T10:15:00Z' }
  ]
};

const WithdrawalRequestDashboard: React.FC = () => {
  const { user } = useAuth();
  // Enhanced state management with proper types
  const [requests, setRequests] = useState<WithdrawalRequest[]>([]);
  const [currentUser, setCurrentUser] = useState<User | null>(null);
  const [selectedRequest, setSelectedRequest] = useState<WithdrawalRequest | null>(null);
  const [showModal, setShowModal] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [actionToPerform, setActionToPerform] = useState<{action: ActionType, request?: WithdrawalRequest} | null>(null);
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterCountry, setFilterCountry] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [showCreateRequest, setShowCreateRequest] = useState(false);
  const [newComment, setNewComment] = useState('');
  const [animateStats, setAnimateStats] = useState(false);
  const [loadingState, setLoadingState] = useState<LoadingState>({ isLoading: false });
  const [confirmDialog, setConfirmDialog] = useState<{
    isOpen: boolean;
    title: string;
    message: string;
    onConfirm: () => Promise<void>;
    variant?: 'danger' | 'warning' | 'info';
  }>({
    isOpen: false,
    title: '',
    message: '',
    onConfirm: async () => {}
  });

  useEffect(() => {
    setAnimateStats(true);
    setTimeout(() => setAnimateStats(false), 1000);
  }, []);

  useEffect(() => {
    const handleEscKey = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        if (showModal) setShowModal(false);
        if (showLoginModal) setShowLoginModal(false);
        if (showCreateRequest) setShowCreateRequest(false);
      }
    };

    document.addEventListener('keydown', handleEscKey);
    return () => document.removeEventListener('keydown', handleEscKey);
  }, [showModal, showLoginModal, showCreateRequest]);

  useEffect(() => {
    if (showModal || showLoginModal || showCreateRequest) {
      document.body.style.overflow = 'hidden';
    } else {
      document.body.style.overflow = 'unset';
    }

    return () => {
      document.body.style.overflow = 'unset';
    };
  }, [showModal, showLoginModal, showCreateRequest]);

  // Map ADFD user roles to withdrawal request roles
  const mapUserRole = (adfdRole: string) => {
    switch (adfdRole) {
      case 'archive_team':
        return 'archive';
      case 'regional_operations':
      case 'head_of_operations':
        return 'operations';
      case 'core_banking':
        return 'core_banking';
      case 'admin':
      case 'loan_administrator':
        return 'loan_admin';
      default:
        return 'loan_admin'; // Default to view-only
    }
  };

  const validateStrictPermission = (action: string, request: WithdrawalRequest | null, user: any) => {
    const userRole = mapUserRole(user?.role || '');

    switch (action) {
      case 'create_request':
        return userRole === 'archive';
      case 'approve':
      case 'reject':
        return userRole === 'operations' && request?.currentStage === 'technical_review';
      case 'disburse':
        return userRole === 'core_banking' && request?.currentStage === 'core_banking';
      default:
        return false;
    }
  };

  const handleLoginForAction = (action: string, request: WithdrawalRequest | null = null) => {
    setActionToPerform({ action, request });
    setShowLoginModal(true);
  };

  const handleLogin = (username: string) => {
    const mockUser = mockUsers[username as keyof typeof mockUsers];
    setCurrentUser(mockUser);
    setShowLoginModal(false);

    if (actionToPerform) {
      executeAction(actionToPerform.action, actionToPerform.request, mockUser);
      setActionToPerform(null);
    }
  };

  const executeAction = (action: string, request: WithdrawalRequest | null, user: any) => {
    const hasPermission = validateStrictPermission(action, request, user);

    if (!hasPermission) {
      alert(`ACCESS DENIED: Only ${getRequiredRole(action)} can perform this action. Your role (${user.role.replace('_', ' ')}) is not authorized.`);
      return;
    }

    switch (action) {
      case 'create_request':
        if (user.role !== 'archive') {
          alert('ACCESS DENIED: Only Archive Team can create new requests.');
          return;
        }
        setShowCreateRequest(true);
        break;
      case 'approve':
        if (user.role !== 'operations') {
          alert('ACCESS DENIED: Only Operations Team can approve requests.');
          return;
        }
        if (request) updateRequestStatus(request.id, 'Approved - Sent to Core Banking', 'core_banking');
        break;
      case 'reject':
        if (user.role !== 'operations') {
          alert('ACCESS DENIED: Only Operations Team can reject requests.');
          return;
        }
        if (request) updateRequestStatus(request.id, 'Rejected by Operations', 'initial_review');
        break;
      case 'disburse':
        if (user.role !== 'core_banking') {
          alert('ACCESS DENIED: Only Core Banking Team can mark disbursements.');
          return;
        }
        if (request) updateRequestStatus(request.id, 'Disbursed', 'disbursed');
        break;
      default:
        alert('Invalid action requested.');
    }
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  const getFilteredRequests = () => {
    let filtered = requests;

    if (filterStatus !== 'all') {
      filtered = filtered.filter(req => req.currentStage === filterStatus);
    }

    if (filterCountry !== 'all') {
      filtered = filtered.filter(req => req.country === filterCountry);
    }

    if (searchTerm) {
      filtered = filtered.filter(req =>
        req.refNumber.toLowerCase().includes(searchTerm.toLowerCase()) ||
        req.beneficiaryName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        req.projectNumber.includes(searchTerm)
      );
    }

    return filtered;
  };

  const getDashboardStats = () => {
    const allRequests = requests;
    const totalRequests = allRequests.length;
    const pendingRequests = allRequests.filter(req => req.currentStage !== 'disbursed').length;
    const avgProcessingTime = Math.round(allRequests.reduce((acc, req) => acc + req.processingDays, 0) / totalRequests);
    const dueSoon = allRequests.filter(req => {
      const valueDate = new Date(req.valueDate);
      const today = new Date();
      const diffDays = Math.ceil((valueDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24));
      return diffDays <= 3 && req.currentStage !== 'disbursed';
    }).length;

    return { totalRequests, pendingRequests, avgProcessingTime, dueSoon };
  };

  const getStatusIcon = (stage: string) => {
    switch (stage) {
      case 'disbursed':
        return <CheckCircle className="w-5 h-5 text-emerald-500" />;
      case 'core_banking':
        return <Clock className="w-5 h-5 text-blue-500" />;
      case 'technical_review':
        return <AlertCircle className="w-5 h-5 text-amber-500" />;
      case 'initial_review':
        return <Pause className="w-5 h-5 text-orange-500" />;
      default:
        return <XCircle className="w-5 h-5 text-red-500" />;
    }
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'low': return 'bg-gray-100 text-gray-800 border-gray-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const updateRequestStatus = (requestId: number, newStatus: string, newStage: any) => {
    setRequests(prev => prev.map(req =>
      req.id === requestId
        ? { ...req, status: newStatus, currentStage: newStage, updatedAt: new Date().toISOString() }
        : req
    ));
  };

  const getRequiredRole = (action: string) => {
    switch (action) {
      case 'create_request':
        return 'Archive Team';
      case 'approve':
      case 'reject':
        return 'Operations Team';
      case 'disburse':
        return 'Core Banking Team';
      default:
        return 'Authorized User';
    }
  };

  const getActionButtonsForRequest = (request: WithdrawalRequest) => {
    const buttons = [];

    if (request.currentStage === 'technical_review') {
      buttons.push(
        <motion.button
          key="approve"
          onClick={() => handleLoginForAction('approve', request)}
          className="text-white p-3 rounded-2xl shadow-lg"
          style={{ backgroundColor: '#4A8B2C' }}
          title="Approve (Operations Team Only)"
          whileHover={{ scale: 1.1, rotate: 5 }}
          whileTap={{ scale: 0.95 }}
          transition={{ duration: 0.2 }}
        >
          <CheckCircle className="w-5 h-5" />
        </motion.button>
      );

      buttons.push(
        <motion.button
          key="reject"
          onClick={() => handleLoginForAction('reject', request)}
          className="text-white p-3 rounded-2xl shadow-lg"
          style={{ backgroundColor: '#DC3545' }}
          title="Reject (Operations Team Only)"
          whileHover={{ scale: 1.1, rotate: -5 }}
          whileTap={{ scale: 0.95 }}
          transition={{ duration: 0.2 }}
        >
          <XCircle className="w-5 h-5" />
        </motion.button>
      );
    }

    if (request.currentStage === 'core_banking') {
      buttons.push(
        <motion.button
          key="disburse"
          onClick={() => handleLoginForAction('disburse', request)}
          className="text-white p-3 rounded-2xl shadow-lg"
          style={{ backgroundColor: '#007CBA' }}
          title="Mark as Disbursed (Core Banking Team Only)"
          whileHover={{ scale: 1.1, rotate: 10 }}
